"""author: katiedate: 03/21/2021"""import pandas as pdimport os# path to data (schools.csv)data_path = '../../dataset_ignore/schools.csv'output_path = '../../datasets_clean/schools_latlon.csv'# pulling data from csv fileif os.path.exists(data_path):    print('acquiring data...')    raw = pd.read_csv(data_path, usecols = [0,1,2,3,4,6,7])        # renaming for convenience    raw.columns = ['lon','lat','id','name','address','town','zip']        # reordering for convenience    raw = raw[['id','name','address','lat','lon','town','zip']]        # removing non-boston    print('cleaning...')    boston = raw[raw['town']=='BOSTON'].drop(columns=['town']).reset_index(drop = True)        # output clean data as csv    print('outputting...')    boston.to_csv(output_path)    print('done')    # rejects if path has been changedelse:    raise FileNotFoundError('invalid path to schools')